mapper.select:
SELECT c.complaint_no AS complaintNo, c.title, c.category, c.status, c.created_date AS createdDate, COUNT(pl.complaint_no) AS likeCount FROM complaint c LEFT JOIN post_like pl ON c.complaint_no = pl.complaint_no
AND c.category = #{category}
AND c.status = #{status}
AND c.complaint_no IN ( SELECT complaint_no FROM complaint_agency WHERE agency_no = #{agencyNo} )
AND (c.title LIKE CONCAT('%', #{search}, '%') OR c.content LIKE CONCAT('%', #{search}, '%'))
GROUP BY c.complaint_no ORDER BY c.complaint_no DESC
