mapper.select:
SELECT c.complaint_no AS complaintNo, c.title, c.content, c.category, c.status, c.created_date AS createdDate, c.address, c.latitude, c.longitude, c.image_path AS imagePath, c.answer, c.is_public AS isPublic, c.like_count AS likeCount, ca.agency_no AS agencyNo, a.agency_name AS agencyName, a.region_code AS regionCode, CASE WHEN a.agency_type = 'LOCAL' THEN a.agency_name ELSE NULL END AS regionName FROM complaint c LEFT JOIN complaint_agency ca ON c.complaint_no = ca.complaint_no LEFT JOIN agency a ON ca.agency_no = a.agency_no
AND c.category = #{category}
AND c.status = #{status}
AND ca.agency_no = #{agencyNo}
AND a.region_code = #{region}
AND (c.title ILIKE CONCAT('%', #{search}, '%') OR c.content ILIKE CONCAT('%', #{search}, '%'))
ORDER BY c.complaint_no DESC

mapper.insert:
INSERT INTO complaint (
    title, content, category, status, is_public, user_no, image_path, created_date, updated_date,
    address, latitude, longitude
)
VALUES (
    #{title}, #{content}, #{category}, #{status}, #{isPublic}, #{userNo}, #{imagePath},
    CURRENT_TIMESTAMP, CURRENT_TIMESTAMP,
    #{address}, #{latitude}, #{longitude}
)

INSERT INTO complaint (
    title, content, category, status, is_public, user_no, created_date,
    address, latitude, longitude, image_path, like_count
) VALUES (
    #{title}, #{content}, #{category}, #{status}, #{isPublic}, #{userNo}, #{createdDate},
    #{address}, #{latitude}, #{longitude}, #{imagePath}, #{likeCount}
)

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM complaint_like
    WHERE complaint_no = #{complaintNo} AND user_no = #{userNo}
)

mapper.update:
UPDATE complaint
SET status = #{status}
WHERE complaint_no = #{complaintNo}

UPDATE complaint
SET answer = #{answer}
WHERE complaint_no = #{complaintNo}

mapper.select:
SELECT COUNT(*)::int
FROM complaint_like
WHERE complaint_no = #{complaintNo} AND user_no = #{userNo}

mapper.update:
UPDATE complaint
SET like_count = COALESCE(like_count, 0) + 1
WHERE complaint_no = #{complaintNo}

UPDATE complaint
SET like_count = GREATEST(COALESCE(like_count, 0) - 1, 0)
WHERE complaint_no = #{complaintNo}

mapper.select:
SELECT DISTINCT c.* FROM complaint c LEFT JOIN complaint_agency ca ON c.complaint_no = ca.complaint_no
AND c.status = #{status}
AND ca.agency_no = #{agencyNo}
ORDER BY c.like_count DESC LIMIT 5

SELECT c.status, COUNT(*) as count FROM complaint c LEFT JOIN complaint_agency ca ON c.complaint_no = ca.complaint_no
AND ca.agency_no = #{agencyNo}
GROUP BY c.status

SELECT category AS name, COUNT(*)::int AS value FROM complaint c LEFT JOIN complaint_agency ca ON c.complaint_no = ca.complaint_no
AND ca.agency_no = #{agencyNo}
GROUP BY category ORDER BY value DESC

SELECT TO_CHAR(d, 'YYYY-MM') AS month, COUNT(CASE WHEN TO_CHAR(c.created_date, 'YYYY-MM') = TO_CHAR(d, 'YYYY-MM') THEN 1 END)::int AS received, COUNT(CASE WHEN TO_CHAR(c.completed_date, 'YYYY-MM') = TO_CHAR(d, 'YYYY-MM') THEN 1 END)::int AS completed FROM ( SELECT generate_series( CURRENT_DATE - INTERVAL '5 months', CURRENT_DATE, '1 month' )::date AS d ) d LEFT JOIN complaint c ON TO_CHAR(c.created_date, 'YYYY-MM') = TO_CHAR(d, 'YYYY-MM')
AND c.category = #{category}
GROUP BY month ORDER BY month ASC
